mapper.select:
select

  distinct


from sys_resource




  order by ${orderByClause}


  limit #{page.limit} offset #{page.offset}*#{page.limit}

SELECT DISTINCT rs.* FROM sys_resource rs LEFT JOIN sys_role_resource rr ON rs.id = rr.resource_id LEFT JOIN sys_role r ON r.id = rr.role_id LEFT JOIN sys_user_role ur ON r.id = ur.role_id LEFT JOIN sys_user u ON u.id = ur.user_id where rs.resource_type = 'url'
and u.id = #{userId,jdbcType=INTEGER}
and r.id = #{roleId,jdbcType=INTEGER}
order by rs.resource_order_num

SELECT DISTINCT rs.* FROM sys_resource rs LEFT JOIN sys_role_resource rr ON rs.id = rr.resource_id LEFT JOIN sys_role r ON r.id = rr.role_id LEFT JOIN sys_user_role ur ON r.id = ur.role_id LEFT JOIN sys_user u ON u.id = ur.user_id where 1=1
and u.id = #{userId,jdbcType=INTEGER}
and r.id = #{roleId,jdbcType=INTEGER}
order by rs.resource_order_num
